mapper.update:
UPDATE product_option
SET stock = stock - #{qty}
WHERE id = #{optionId}
  AND stock >= #{qty}

mapper.insert:
INSERT INTO product_option (
    product_id, option_type_id, option_value, extra_price, stock
) VALUES (
    #{productId}, #{optionTypeId}, #{optionValue}, #{extraPrice}, #{stock}
)

mapper.update:
UPDATE product_option
SET option_type_id = #{optionTypeId},
    option_value   = #{optionValue},
    extra_price    = #{extraPrice},
    stock          = #{stock}
WHERE id = #{id}
