mapper.select:
SELECT status
FROM deliveries
WHERE order_id = #{orderId}

SELECT 
    sd.*, p.name AS productName, m.nickname AS memberNickname,
    p.category_id AS categoryId  
FROM settlement_detail sd
JOIN product p ON sd.product_id = p.product_id
JOIN member m ON sd.member_id = m.id
WHERE 1 = 1
 AND sd.type = #{type} 
 AND sd.created_at BETWEEN #{start} AND #{end} 
 AND sd.member_id = #{memberId} 
 AND sd.product_id = #{productId} 
 AND p.category_id = #{categoryId}  
 AND sd.status = #{status}
