mapper.insert:
INSERT INTO member (
email, password, name, nickname, phone, address,
provider, provider_id, is_verified, role, grade
) VALUES (
#{email}, #{password}, #{name}, #{nickname}, #{phone}, #{address},
#{provider}, #{providerId}, false, 'USER', #{grade}
)

mapper.update:
UPDATE member
SET verified = 1
WHERE email = #{email}

UPDATE member
SET password = #{password}
WHERE email = #{email}

